import React, { useState } from "react";

const Edit = ({ expense }) => {
  const [exp, setExp] = useState(expense.amount);
  const [description, setDescription] = useState(expense.description);

  const editAmount = async (id) => {
    try {
        const body = {exp, description};
        const res = await fetch(`http://localhost:5000/expense/${id}`, {
            method: "PUT",
            headers: {"Content-type": "application/json"},
            body: JSON.stringify(body)
        })
        window.location = "/";
    } catch (error) {
        console.error(error.message);
    }
  }

  return (
    <>
      <button
        type="button"
        className="btn btn-warning"
        data-toggle="modal"
        data-target={`#id${expense.id}`}
      >
        Edit
      </button>

      {/* <div className="modal" id={`id${expense.id}`}></div> */}
      <div
        className="modal fade"
        id={`id${expense.id}`}
        tabindex="-1"
        role="dialog"
        aria-labelledby="exampleModalCenterTitle"
        aria-hidden="true"
      >
        <div className="modal-dialog modal-dialog-centered" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLongTitle">
                Edit Expense
              </h5>
              <button
                type="button"
                className="close"
                data-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
            </div>
            <div className="modal-body">
              <input
                type="text"
                className="form-control my-3"
                value={exp}
                onChange={e => setExp(e.target.value)}
              ></input>
              {/* <input
                type="text"
                className="form-control"
                value={description}
              ></input> */}
            </div>
            <div className="modal-footer">
              <button
                type="button"
                className="btn btn-secondary"
                data-dismiss="modal"
              >
                Close
              </button>
              <button type="button" className="btn btn-warning"
              data-dismiss="modal"
              onClick={() => editAmount(expense.id)}
              >
                Edit
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Edit;
